// Get data from both previous nodes
const threatData = $('Code in JavaScript').all();
const coordinatesData = $('AI Agent1').all();

// Merge the data
return threatData.map((threat, index) => {
  try {
    // Get coordinates for this item
    const coordItem = coordinatesData[index];
    let coordinates;

    // Parse coordinates if they're in string format
    if (typeof coordItem.json.output === 'string') {
      // Remove markdown code blocks and extra formatting
      let cleanString = coordItem.json.output
        .replace(/```json\n?/g, '')  // Usuń ```json
        .replace(/```\n?/g, '')       // Usuń ```
        .replace(/\n/g, '')
        .replace(/\\/g, '')
        .trim();

      coordinates = JSON.parse(cleanString);
    } else if (coordItem.json.latitude && coordItem.json.longitude) {
      // If already parsed
      coordinates = {
        latitude: coordItem.json.latitude,
        longitude: coordItem.json.longitude
      };
    } else {
      coordinates = coordItem.json;
    }

    // Merge everything together
    return {
      json: {
        type_of_threat: "road accident",
        summary: threat.json.summary,
        location: threat.json.location,
        date: threat.json.date,
        url: threat.json.url,
        latitude: coordinates.latitude,
        longitude: coordinates.longitude
      }
    };
  } catch (error) {
    console.error(`Error at index ${index}:`, error);

    // Return with null coordinates if error
    return {
      json: {
        ...threat.json,
        latitude: null,
        longitude: null,
        error: `Coordinate parsing error: ${error.message}`
      }
    };
  }
});

// Dla każdego elementu z inputu
return items.map(item => {
  let data = item.json.data;

  // Usuń niepotrzebne elementy
  data = data
    // Usuń linki do obrazków
    .replace(/!\[Image \d+:.*?\]\(https?:\/\/[^\)]+\)/g, '')
    // Usuń bloki reklam "reklama\n\nZobacz więcej\n..." wraz z całą zawartością do następnego paragrafu
    .replace(/reklama\n\nZobacz więcej\n[\s\S]*?(?=\n\n[A-ZŁĆĘŚĄŻŹŃ]|\n\n\*|\n\n###|\n\n\[|$)/g, '')
    // Usuń sekcje "Te artykuły mogą Cię zainteresować:"
    .replace(/\*\*Te artykuły mogą Cię zainteresować:\*\*[\s\S]*$/g, '')
    // Usuń linki typu [tekst](url)
    .replace(/\[([^\]]+)\]\(https?:\/\/[^\)]+\)/g, '$1')
    // Usuń pozostałe linki markdown
    .replace(/\* \[.*?\]\(.*?\)/g, '')
    // Usuń wielokrotne puste linie
    .replace(/\n{3,}/g, '\n\n')
    // Usuń białe znaki na początku i końcu
    .trim();

  return {
    json: {
      title: data.match(/Title: (.*?)\n/)?.[1] || '',
      url: item.json.data.match(/URL Source: (.*?)\n/)?.[1] || '',
      content: data
        .replace(/Title: .*?\n/, '')
        .trim()
    }
  };
});
